	function updateCart() {
    const cartItemsElement = document.getElementById("cart-items");
    const totalPriceElement = document.getElementById("total-price");
    const duplicatedTotalPriceElement = document.getElementById("duplicated-total-price"); // Reference for duplicated total price
    const discountAmountElement = document.getElementById("discount-amount");

    // Clear the current list
    cartItemsElement.innerHTML = "";

    // Add items to the list with a remove button
    cart.forEach((item, index) => {
        const listItem = document.createElement("li");
        listItem.textContent = `${item.name} - â‚±${item.price.toFixed(2)} `;

        // Create remove button
        const removeButton = document.createElement("button");
        removeButton.textContent = "Remove";
        removeButton.onclick = () => removeProduct(index);

        // Append the remove button to the list item
        listItem.appendChild(removeButton);

        // Append the list item to the cart
        cartItemsElement.appendChild(listItem);
    });

    // Calculate the total price with or without discount
    let finalPrice = totalPrice;
    let discountAmount = 0;
    if (discountApplied) {
        discountAmount = totalPrice * 0.2; // 20% discount
        finalPrice = totalPrice - discountAmount;
    }

    // Display the total price (with discount if applied)
    totalPriceElement.textContent = `â‚±${finalPrice.toLocaleString('en-PH', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}`;

    // Display the duplicated total price
    duplicatedTotalPriceElement.textContent = `â‚±${finalPrice.toLocaleString('en-PH', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}`;

    // Display the discount amount if a discount is applied
    if (discountAmount > 0) {
        discountAmountElement.textContent = `Discount Applied: â‚±${discountAmount.toLocaleString('en-PH', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}`;
    } else {
        discountAmountElement.textContent = `Discount : â‚±${discountAmount.toLocaleString('en-PH', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}`;
    }
}

